import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function RegistroRestauranteTailwind() {
  const [formData, setFormData] = useState({
    nombre: '',
    direccion: '',
    telefono: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ 
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      toast.error('Las contraseñas no coinciden');
      return;
    }

    setLoading(true);
    try {
      await axios.post('/api/restaurante/registro', formData);
      toast.success('Restaurante registrado exitosamente');
      // Redireccionar al login tras el registro
      setTimeout(() => navigate('/login-restaurante'), 2000);
    } catch (err) {
      toast.error('Error al registrar el restaurante');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <ToastContainer />
      <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
        <div className="sm:mx-auto sm:w-full sm:max-w-sm">
          <img
            alt="Your Company"
            src="https://tailwindcss.com/plus-assets/img/logos/mark.svg?color=indigo&shade=600"
            className="mx-auto h-10 w-auto"
          />
          <h2 className="mt-10 text-center text-2xl font-bold tracking-tight text-gray-900">
            Registro de Restaurante
          </h2>
        </div>

        <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Campos del formulario */}
            {['nombre', 'direccion', 'telefono', 'email', 'password', 'confirmPassword'].map((field) => (
              <div key={field}>
                <label className="block text-sm font-medium text-gray-900 capitalize">
                  {field === 'confirmPassword' ? 'Confirmar contraseña' : field}
                </label>
                <div className="mt-2">
                  <input
                    type={field.includes('password') ? 'password' : field === 'email' ? 'email' : 'text'}
                    name={field}
                    value={formData[field]}
                    required
                    className="block w-full rounded-md bg-white px-3 py-1.5 text-gray-900 
                              placeholder:text-gray-400 outline outline-1 outline-gray-300
                              focus:outline-2 focus:outline-indigo-600 sm:text-sm"
                    onChange={handleChange}
                  />
                </div>
              </div>
            ))}

            {/* Botón de envío */}
            <div>
              <button
                type="submit"
                disabled={loading}
                className={`flex w-full justify-center rounded-md px-3 py-1.5 text-sm font-semibold text-white shadow 
                            ${loading ? 'bg-gray-400' : 'bg-indigo-600 hover:bg-indigo-500'}
                            focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 
                            focus-visible:outline-indigo-600`}
              >
                {loading ? 'Registrando...' : 'Registrar'}
              </button>
            </div>
          </form>

          <p className="mt-10 text-center text-sm text-gray-500">
            ¿Ya tienes cuenta?{' '}
            <Link to="/Login" className="font-semibold text-indigo-600 hover:text-indigo-500">
              Iniciar Sesión
            </Link>
          </p>
        </div>
      </div>
    </>
  );
}
