import React, { useState } from 'react';
import './Login.css';
import { useNavigate, Link } from 'react-router-dom'; // Importar hook para navegación
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Hook de navegación

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/api/clientes/buscar', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (data.success) {
        localStorage.setItem('user', JSON.stringify(data.user));
        toast.success('¡Inicio de sesión exitoso! Redirigiendo...');
        setTimeout(() => navigate('/HomeUsuario'), 2000);
      } else {
        alert(data.error);
      }
    } catch (error) {
      alert('Error al iniciar sesión');
      console.error(error);
    }
  };

  return (
    <>
    <ToastContainer position="top-right" autoClose={3000} />
    <div className='login-container'>
      <div className="login-card">
      <img alt="Your Company"
          src="https://tailwindcss.com/plus-assets/img/logos/mark.svg?color=indigo&shade=600"
          className="mx-auto h-10 w-auto" />
      <h2 className="login-title">Iniciar Sesión - Usuario</h2>
      <div className="form-group">
            <label className="label" htmlFor="email">Correo Electrónico</label>
            <input
              className="input"
              type="email"
              placeholder='Email'
              value={email} onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
      <div className="form-group">
            <label className="label" htmlFor="password">Contraseña</label>
            <input
              className="input"
              type="password"
              placeholder='Contraseña'
              value={password} onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
      <button className="button" onClick={handleLogin}>Iniciar sesión</button>
      <p className="register-text">
          ¿Aún no tienes cuenta? <Link to="/Registro">Registrarse</Link>
        </p>
      </div>
    </div>
    </>
  );
}

export default Login;
