import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { BellIcon } from '@heroicons/react/24/outline';
import './HomeUsuario.css';

function HomeUsuario() {
  const [user, setUser] = useState({
    user: 'Usuario Genérico',
    email: 'usuario@example.com',
    imageUrl: 'https://via.placeholder.com/150'
  });
  const navigate = useNavigate();

  useEffect(() => {
    // Si guardaste los datos del usuario en localStorage, se recuperan aquí
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  // Función para cerrar sesión
  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  // Función para ir a ver pedidos (navega a OrdenesTabla)
  const verPedidos = () => {
    navigate('/OrdenesTabla');
  };

  return (
    <div className="home-container">
      {/* Barra de navegación */}
      <nav className="home-nav">
        <div className="logo">
        <img alt="Your Company"
          src="https://tailwindcss.com/plus-assets/img/logos/mark.svg?color=indigo&shade=600"
          className="mx-auto h-10 w-auto" />
        </div>
        <div className="nav-links">
          <Link to="/" className="nav-link">Inicio</Link>
          <button onClick={verPedidos} className="nav-link">Ver Pedidos</button>
        </div>
        <div className="user-info">
          <BellIcon className="notification-icon" />
          <img src={user.imageUrl} alt="User" className="user-avatar" />
          <span className="user-name">{user.name}</span>
          <button onClick={handleLogout} className="logout-button">Cerrar Sesión</button>
        </div>
      </nav>

      {/* Encabezado */}
      <header className="home-header">
        <h1>Bienvenido {user.name}</h1>
      </header>

    </div>
  );
}

export default HomeUsuario;
