import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { BellIcon } from '@heroicons/react/24/outline';
import './HomeUsuario.css';
import OrdenCard from './OrdenCard';
import './OrdenesTabla.css';

function HomeUsuario() {
  const [ordenes, setOrdenes] = useState([]);
  const [user, setUser] = useState({
    name: 'Usuario Gen√©rico',
    email: 'usuario@example.com',
    imageUrl: 'https://via.placeholder.com/150'
  });
  const navigate = useNavigate();

  useEffect(() => {
    // Si guardaste los datos del usuario en localStorage, se recuperan aqu√≠
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }

    fetch('http://localhost:3001/api/orden/buscar')
      .then(res => res.json())
      .then(data => {
        console.log('√ìrdenes recibidas:', data); 
        setOrdenes(data);
      })
      .catch(err => console.error('Error al cargar √≥rdenes', err));

  }, []);

  // Funci√≥n para cerrar sesi√≥n
  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  const verDetalles = (ordenId) => {
    navigate(`/DetalleOrden/${ordenId}`);
  };

  return (
    <div className="home-container">
      {/* Barra de navegaci√≥n */}
      <nav className="home-nav">
        <div className="logo">
        <img src="/images/logo.jpg" alt="SalvEat" className="mx-auto mb-4 h-12" />
        </div>
        <div className="user-info">
          <BellIcon className="notification-icon" />
          <img src={user.imageUrl} alt="User" className="user-avatar" />
          <span className="user-name">{user.name}</span>
          <button onClick={handleLogout} className="logout-button">Cerrar Sesi√≥n</button>
        </div>
      </nav>

      {/* Encabezado */}
      <header className="home-header">
        <h1 className="text-3xl font-bold text-gray-800">¬°Bienvenido, {user.name}!</h1>
        <p className="text-gray-500 mt-1">Explora las ofertas especiales de hoy antes de que se agoten üçΩÔ∏è</p>
      </header>

      <div className="contenedor-ordenes">
      <h2>Lista de √ìrdenes</h2>
      <div className="bg-indigo-100 border-l-4 border-indigo-500 text-indigo-700 p-4 rounded mb-6 mx-4">
  <p><strong>Tip:</strong> Las √≥rdenes con descuento tienen tiempo limitado. ¬°Aprovecha antes que desaparezcan!</p>
</div>

      {ordenes.length === 0 ? (
        <p>No hay √≥rdenes disponibles</p>
      ) : (
        <div className="ordenes-grid">
          {ordenes.map((orden) => (
            <OrdenCard key={orden.orden_id} orden={orden} verDetalles={verDetalles} />
          ))}
        </div>
      )}
    </div>


    </div>
  );
}

export default HomeUsuario;
