import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Menu, MenuButton, MenuItem, MenuItems } from '@headlessui/react';
import { BellIcon } from '@heroicons/react/24/outline';

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

const navigation = [
  { name: 'Dashboard', path: '/restaurant-dashboard' },
  { name: 'Publicar Pedidos', path: '/publish-orders' },
  { name: 'Ver Pedidos', path: '/view-orders' },
];

export default function RestaurantDashboard() {
  // Se inicializa el usuario leyendo cada valor desde localStorage
  const [user] = useState(() => {
    const storedName = localStorage.getItem('name');
    const storedEmail = localStorage.getItem('email');
    const storedRestauranteId = localStorage.getItem('restaurante_id');
    // Imagen por defecto (puedes modificarla según tu conveniencia)
    const defaultImageUrl =
      'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-1.2.1&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80';
    return {
      name: storedName || 'Restaurante Genérico',
      email: storedEmail || 'restaurante@example.com',
      restaurante_id: storedRestauranteId || '',
      imageUrl: defaultImageUrl,
    };
  });

  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login-restaurante');
  };  

  const userNavigation = [
    { name: 'Perfil', path: '/perfil' },
    { name: 'Ajustes', path: '/ajustes' },
    { name: 'Cerrar sesión', onClick: handleLogout },
  ];

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Barra de Navegación Desktop */}
      <nav className="bg-gray-800">
        <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
          <div className="flex h-16 items-center justify-between">
            {/* Sección izquierda: Logo y enlaces */}
            <div className="flex items-center">
              <div className="shrink-0">
                <img
                  alt="Your Company"
                  src="https://tailwindcss.com/plus-assets/img/logos/mark.svg?color=indigo&shade=500"
                  className="h-8 w-auto"
                />
              </div>
              <div className="ml-10 flex items-center space-x-4">
                {navigation.map((item) => (
                  <Link
                    key={item.name}
                    to={item.path}
                    className="text-gray-300 hover:bg-gray-700 hover:text-white rounded-md px-3 py-2 text-sm font-medium"
                  >
                    {item.name}
                  </Link>
                ))}
              </div>
            </div>
            {/* Sección derecha: Notificaciones y menú de usuario */}
            <div className="flex items-center">
              <button
                type="button"
                className="relative rounded-full bg-gray-800 p-1 text-gray-400 hover:text-white focus:ring-2 focus:ring-white focus:ring-offset-2 focus:ring-offset-gray-800"
              >
                <span className="sr-only">Ver notificaciones</span>
                <BellIcon className="h-6 w-6" aria-hidden="true" />
              </button>
              <Menu as="div" className="relative ml-3">
                <div>
                  <MenuButton className="flex max-w-xs items-center rounded-full bg-gray-800 text-sm focus:ring-2 focus:ring-white focus:ring-offset-2 focus:ring-offset-gray-800">
                    <span className="sr-only">Abrir menú de usuario</span>
                    <img alt="" src={user.imageUrl} className="h-8 w-8 rounded-full" />
                  </MenuButton>
                </div>
                <MenuItems className="absolute right-0 z-10 mt-2 w-48 origin-top-right rounded-md bg-white py-1 shadow-lg ring-1 ring-black/5 focus:outline-none">
                  {userNavigation.map((item) => (
                    <MenuItem key={item.name}>
                      {({ active }) =>
                        item.onClick ? (
                          <button
                            onClick={item.onClick}
                            className={classNames(
                              active ? 'bg-gray-100' : '',
                              'block w-full px-4 py-2 text-left text-sm text-gray-700'
                            )}
                          >
                            {item.name}
                          </button>
                        ) : (
                          <Link
                            to={item.path}
                            className={classNames(
                              active ? 'bg-gray-100' : '',
                              'block px-4 py-2 text-sm text-gray-700'
                            )}
                          >
                            {item.name}
                          </Link>
                        )
                      }
                    </MenuItem>
                  ))}
                </MenuItems>
              </Menu>
            </div>
          </div>
        </div>
      </nav>

      {/* Encabezado Secundario */}
      <header className="bg-white shadow-sm">
        <div className="mx-auto max-w-7xl px-4 py-6 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold tracking-tight text-gray-900">
            Dashboard del Restaurante
          </h1>
          <p className="mt-2 text-xl text-gray-700">
            Bienvenido, {user.name}.
          </p>
        </div>
      </header>

      {/* Contenido Principal */}
      <main>
        <div className="mx-auto max-w-7xl px-4 py-6 sm:px-6 lg:px-8">
          <p className="text-gray-700">
            Aquí podrás gestionar tus pedidos, ver estadísticas y realizar otras acciones.
          </p>
          {/* Agrega aquí las secciones o componentes que necesites */}
        </div>
      </main>
    </div>
  );
}
