import React, { useState } from 'react';
import './Registro.css';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Registro() {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [telefono, setTelefono] = useState('');
  const [password, setPassword] = useState('');
  const [clienteId, setClienteId] = useState(null);
  const navigate = useNavigate();

  const handleRegistro = async () => {
    console.log("Botón de registro presionado.");
    try {
      const response = await fetch('http://localhost:3001/api/clientes/agregar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nombre, email, telefono, password }),
      });

      const data = await response.json();
      if (data.success) {
        setClienteId(data.id); // Guardamos ID para futuras acciones
        toast.success('¡Registro exitoso! Redirigiendo...');
        setTimeout(() => navigate('/'), 2000);
      } else {
        alert('Error al registrar cliente');
      }
    } catch (error) {
      console.error('Error en el registro:', error);
    }
  };        

  return (
    <div className='register-container'>
      <div className="register-card">
      <img alt="Your Company"
          src="https://tailwindcss.com/plus-assets/img/logos/mark.svg?color=indigo&shade=600"
          className="mx-auto h-10 w-auto" />
      <h2 className="register-title">Registro - Usuario</h2>
      <div className="form-group"> 
      <label className="label" htmlFor="nombre">Nombre completo</label>
      <input 
      className="input" 
      type='text' 
      placeholder='Nombre completo'
      required 
      value={nombre} onChange={(e) => setNombre(e.target.value)} />
      </div>
      <div className="form-group"> 
      <label className="label" htmlFor="email">Correo Electrónico</label>
      <input 
      className="input" 
      type='email' 
      placeholder='Email' 
      value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>
      <div className="form-group"> 
      <label className="label" htmlFor="telefono">Teléfono</label>
      <input 
      className="input" 
      type='text' 
      placeholder='Teléfono' 
      value={telefono} onChange={(e) => setTelefono(e.target.value)} />
      </div>
      <div className="form-group"> 
      <label className="label" htmlFor="password">Contraseña</label>
      <input 
      className="input" 
      type='password' 
      placeholder='Contraseña' 
      value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>
      <button className="button" onClick={handleRegistro}>Registrarse</button>
      </div>
      <ToastContainer position="top-right" autoClose={3000} />
    </div>
  );
}


export default Registro;
